function pasoUno(){
    textoPantalla = document.querySelector('.contCuadroPantalla.pasoUno');
    
    contTexto_activo = document.querySelector('.contTexto.pasoUno');
    contTexto_proximo = document.querySelector('.contTexto.pasoDos');

    btnClick = document.querySelector('.pasoUno .btnClick');

    btnClick.onclick = function() {
        if( isMobile() ) enter_primerPass = primerPass.value;
        if( ok_primerPass == enter_primerPass ){
            textoPantalla.classList.add('animOcultar');

            contTexto_activo.classList.remove('activo');
            contTexto_proximo.classList.add('activo');

            front_primerPass = "";
            enter_primerPass = "";

            textoPantalla.classList.remove('activo');

            pasoDos();
        }else{
            document.querySelector('.contCuadroPantalla.pasoUno .pantallaTexto.Top p').innerHTML = "INGRESÃ“ UNA CLAVE INCORRECTA<br />VUELVA A INGRESARLA<br />LUEGO PRESIONE CONTINUAR";
            document.querySelector('.contCuadroPantalla.pasoUno .pantallaTexto.btn').classList.remove('siguiente');
            enter_primerPass = "";
            front_primerPass = "";
            primerPass.value = front_primerPass;
        }
    };

    textoPantalla.classList.add("activo");
    textoPantalla.classList.add('anim');
}
function pasoDos(){
    textoPantalla = document.querySelector('.contCuadroPantalla.pasoDos');

    contTexto_activo = document.querySelector('.contTexto.pasoDos');
    contTexto_proximo = document.querySelector('.contTexto.pasoTres');

    textoPantalla.classList.add("activo");
    textoPantalla.classList.add('anim');

    document.querySelector('.cuadroMano').classList.remove('num1');
    document.querySelector('.cuadroMano').classList.add('num2');

    btnClick = document.querySelector('.pasoDos .btnClick');

    if( document.getElementById('idContenedorSimulador').classList.contains('movimientos') ){

        document.querySelector('.contCuadroPantalla.pasoDos .pantallaTexto.btn.num-8').classList.add('siguiente');

        btnClick.onclick = function() {
            textoPantalla.classList.add('animOcultar');

            contTexto_activo.classList.remove('activo');
            contTexto_proximo.classList.add('activo');

            textoPantalla.classList.remove('activo');

            pasoTres();
        };

    }else{

        etapaPasoDos = 1;

        btnClick.onclick = function() {
            if(etapaPasoDos == 1){
                document.querySelector('.contCuadroPantalla.pasoDos .pantallaTexto.btn').classList.remove('siguiente');
                if( isMobile() ) enter_primerPass = segundoPass.value;
                if( ok_primerPass == enter_primerPass ){
                    contTexto_activo.classList.remove('activo');
                    contTexto_proximo.classList.add('activo');

                    enter_primerPass = "";
                    front_primerPass = "";

                    etapaPasoDos = 2;
                    document.querySelector('.contCuadroPantalla.pasoDos .pantallaTexto.Top p').innerHTML = "INGRESE LA NUEVA CLAVE<br />** RECUERDELA PARA EL FUTURO **<br />LUEGO PRESIONE CONTINUAR";
                    if( isMobile() ) tercerPass.focus();
                }else{
                    document.querySelector('.contCuadroPantalla.pasoDos .pantallaTexto.Top p').innerHTML = "** CLAVE INCORRECTA **<br />VUELVA A INGRESARLA";
                    enter_primerPass = "";
                    front_primerPass = "";
                    segundoPass.value = front_primerPass;
                }
            }else if( etapaPasoDos == 2){
                if(isMobile()) enter_primerPass = tercerPass.value;
                if( ok_segundoPass == enter_primerPass ){
                    enter_primerPass = "";
                    front_primerPass = "";

                    etapaPasoDos = 3;
                    document.querySelector('.contCuadroPantalla.pasoDos .pantallaTexto.btn').classList.remove('siguiente');
                    document.querySelector('.contCuadroPantalla.pasoDos .pantallaTexto.Top p').innerHTML = "INGRESE OTRA VEZ LA NUEVA CLAVE<br />** RECUERDELA PARA EL FUTURO **<br />LUEGO PRESIONE CONTINUAR";

                    if( isMobile() ) cuartoPass.focus();
                }else{
                    enter_primerPass = "";
                    front_primerPass = "";
                    tercerPass.value = front_primerPass;
                }
            }else if( etapaPasoDos == 3){
                if(isMobile()) enter_primerPass = cuartoPass.value;
                if( ok_segundoPass == enter_primerPass ){
                    enter_primerPass = "";
                    front_primerPass = "";

                    contTexto_activo = document.querySelector('.contTexto.pasoTres');
                    contTexto_proximo = document.querySelector('.contTexto.pasoCuatro');
                    
                    textoPantalla.classList.add('animOcultar');

                    contTexto_activo.classList.remove('activo');
                    contTexto_proximo.classList.add('activo');

                    textoPantalla.classList.remove('activo');

                    pasoTres();
                }else{
                    enter_primerPass = "";
                    front_primerPass = "";
                    cuartoPass.value = front_primerPass;
                }
            }
        };
    }
}
